import { Account_db } from "../../services/db/RDG/Account_db";
import { ServerSocket } from "../../services/socket/SocketServer";
import { Room } from "../Game/Room";
import { AccountManager } from "./AccountManager";

export class Account{
    private id:number = 0
    private name:string
    private password:string
    private avatar:string = 'data:image/jpeg;base64,'
    private clientId:string = ''
    private isGuest:boolean = false;
    private socketId:string = ''
    private score:number = 0
    private gameLost:number = 0;
    private gameWon:number = 0;
    private ping:number = 0;
    private activeInRoom:Room = undefined!

    constructor(name:string,password:string){
        this.name = name
        this.password = password
    }
    save(){
        let newAcc = new Account_db()
        newAcc.setAvatar(this.avatar)
        newAcc.setPassword(this.password)
        newAcc.setScore(this.score)
        newAcc.setName(this.name)
        newAcc.setGameWon(this.gameWon)
        newAcc.setGameLost(this.gameLost)
        console.log('updatoval:')
        console.log(newAcc)
        newAcc.update()

    }
    
    addPing(){
        let acc = this
        setInterval(function(){
            acc.setPing(acc.getPing()+1)
            },5000)
    }
    public getScore() : number {
        return this.score
    }
    public setScore(newScore:number){
        this.score  = newScore
    }
    public getName() : string {
        return this.name
    }
    public setName(newName:string){
        this.name  = newName
    }
    public getPassword() : string {
        return this.password
    }
    public setPassword(newPassword:string){
        this.password  = newPassword
    }
    public getAvatar() : string {
        return this.avatar
    }
    public setAvatar(newAvatar:string){
        this.avatar  = newAvatar
    }
    public getClientId() : string {
        return this.clientId
    }
    public setClientId(newId:string){
        this.clientId = newId
    }
    public getIsGuest() {
        return this.isGuest
    }
    public setIsGuest(is:boolean){
        this.isGuest = is
    }
    public getSocketId() : string {
        return this.socketId
    }
    public setSocketId(newId:string){
        this.socketId = newId
    }
    public getGameWon() : number {
        return this.gameWon
    }
    public setGameWon(newScore:number){
        this.gameWon  = newScore
    }
    public getGameLost() : number {
        return this.gameLost
    }
    public setGameLost(newScore:number){
        this.gameLost  = newScore
    }
    public getPing() : number {
        return this.ping
    }
    public setPing(newScore:number){
        this.ping = newScore
    }
    public getId(){
        return this.id
    }
    public setId(newId:number){
        this.id = newId
    }
    public  getActiveInRoom(){
        return this.activeInRoom
    }
    public setActiveInRoom(room:Room){
        this.activeInRoom = room
    }
}